import React, { useState, useEffect } from 'react'
import '../App.css';
import { fetchCharacters } from '../Callapi/Helper';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css'

const Favorites = () => {
  const [allCharacter, setAllcharacter] = useState('');


  const userCharacters = async () => {
    let myData = await JSON.parse(localStorage.getItem("item"));
    console.log("myData", myData)
    setAllcharacter(myData);
  };

  useEffect(() => {
    userCharacters();
  }, [])

  const removeItem=()=>{
    console.log("chtena")
    var storedNames = JSON.parse(localStorage.getItem("item"));
    console.log("storedNames", storedNames);
    // here you need to make a loop to find the index of item to delete
    var indexToRemove = 1;

    //remove item selected, second parameter is the number of items to delete 
    storedNames.slice(indexToRemove, 1);

   // Put the object into storage
   localStorage.setItem('item', JSON.stringify(storedNames));
   console.log(localStorage.getItem("item"));
  }

  return (
    <div className="container mt-5 px-2">

      <div className="table-responsive">
        <table className="table table-striped table-hover" style={{ border: "0.5px solid #ccc9c9" }}>
          <thead>
            <tr className="bg-light">
              <th scope="col" width="5%">Sir No.</th>
              <th scope="col" width="5%">Name</th>
              <th scope="col" width="20%">Status</th>
              <th scope="col" width="10%">Species</th>
              <th scope="col" width="20%">Location</th>
              <th scope="col" width="20%">Gender</th>
              <th scope="col" width="20%">Action</th>
            </tr>
          </thead>
          {allCharacter ?
            <tbody>

              {allCharacter.map((item) => (
                <tr key={item.id}>
                  <td key={item.id}>{item?.id}</td>
                  <td key={item.id}>{item?.name}</td>
                  <td key={item.id}><i className="fa fa-check-circle-o green" /><span className="ms-1">{item?.status}</span></td>
                  <td key={item.id}><img src="https://i.imgur.com/VKOeFyS.png" width={25} /> {item?.species}</td>
                  <td key={item.id}>{item?.location.name}</td>
                  <td className="text-end" key={item.id}><span className="fw-bolder">{item?.gender}</span> <i className="fa fa-ellipsis-h ms-2" /></td>
                  <td key={item.id}><button className="btn btn-info" onClick={()=>removeItem()}>Remove from Favorite</button></td>
                </tr>
              ))}


            </tbody> :
            <tbody>
              <tr>
                <td>No data Found</td>
              </tr>
            </tbody>
          }

        </table>
      </div>
    </div>
  )
}

export default Favorites
