import React, { useState, useEffect } from 'react'
import '../App.css';
import Modal from 'react-modal';
import ReactDOM from 'react-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css'

const Favorites = () => {
  const [allCharacter, setAllcharacter] = useState('');
  const [removeId, setremoveId] = useState('');

  const customStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
    },
  };

  let subtitle;
  const [modalIsOpen, setIsOpen] = React.useState(false);

  function openModal(id) {
    setIsOpen(true);
    setremoveId(id);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = '#f00';
  }

  function closeModal() {
    setIsOpen(false);
  }

  const userCharacters = async () => {
    let myData = await JSON.parse(localStorage.getItem("item"));
    setAllcharacter(myData);
  };

  useEffect(() => {
    userCharacters();
    if (localStorage.getItem("userfavorite") != '') {
      toast.success(localStorage.getItem("userfavorite"));
      localStorage.removeItem("userfavorite");
    }
  }, [])


  const removeItem = () => {
    try {
      var retrievedObj = JSON.parse(localStorage.getItem("item"));
      const idToRemove = removeId;

      const filteredPeople = retrievedObj.filter((item) => item.removeId !== idToRemove);
      localStorage.setItem("item", JSON.stringify(filteredPeople));
      userCharacters();
      toast.success("User removed successfully from favorites");
    } catch {
      toast.error("Error in user remove from favorites");
    }
  }

  return (
    <div className="container mt-5 px-2">

      <div className="table-responsive">
        <table className="table table-striped table-hover" style={{ border: "0.5px solid #ccc9c9" }}>
          <thead>
            <tr className="bg-light">
              <th scope="col" width="5%">Sir No.</th>
              <th scope="col" width="5%">Name</th>
              <th scope="col" width="20%">Status</th>
              <th scope="col" width="10%">Species</th>
              <th scope="col" width="20%">Location</th>
              <th scope="col" width="20%">Gender</th>
              <th scope="col" width="20%">Action</th>
            </tr>
          </thead>
          {allCharacter ?
            <tbody>

              {allCharacter.map((item) => (
                <tr key={item.id}>
                  <td key={item.id}>{item?.id}</td>
                  <td key={item.id}>{item?.name}</td>
                  <td key={item.id}><i className="fa fa-check-circle-o green" /><span className="ms-1">{item?.status}</span></td>
                  <td key={item.id}><img src="https://i.imgur.com/VKOeFyS.png" width={25} /> {item?.species}</td>
                  <td key={item.id}>{item?.location.name}</td>
                  <td className="text-end" key={item.id}><span className="fw-bolder">{item?.gender}</span> <i className="fa fa-ellipsis-h ms-2" /></td>
                  <td key={item.id}><button className="btn btn-info" onClick={openModal(item?.id)}>Remove from Favorite</button></td>
                </tr>
              ))}


            </tbody> :
            <tbody>
              <tr>
                <td>No data Found</td>
              </tr>
            </tbody>
          }

        </table>
      </div>
      <ToastContainer />
      <Modal
        isOpen={modalIsOpen}
        onAfterOpen={afterOpenModal}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <h6 ref={(_subtitle) => (subtitle = _subtitle)}>Are you sure you wanna remove from favorites ?</h6><br/>
        <button onClick={closeModal} className="btn btn-danger" style={{marginRight: "10px"}}>Close</button>
        <button onClick={() => removeItem()} className="btn btn-success">Confirm</button>
      </Modal>
    </div>
  )
}

export default Favorites
